import { gql } from "@apollo/client";

const query = gql`
    query getSingleNumber($name: String!) {
        number(name: $name) {
        name
        phone
        }
    }
`;

// v: {
//    "name": "A"
// }

export default useQuery = (variable) => {
    return { data, loading, error, refetch } = useQuery(query,{variables:variable});
}
